const VIMPUdpTransfer = require('protocols/vimp/transfers/vimpUdpTransfer');

class LocalDiscoverMulticastTransfer extends VIMPUdpTransfer {
	get clientId() {
		return `${VIServer.nameSpace}_local_discoverer_${VIServer.ip}:${this.udpPort}<->${this.deviceUdpPort}`;
	}
}

module.exports = LocalDiscoverMulticastTransfer;
