/*

	Clase base de transporte MQTT

*/

//Clase base del transmisor
const MQTTTransfer = require('./mqtt');

const { msgPackSerialize, msgPackDeserialize } = require('../protocols/serializers');

class VIMPMQTTTransfer extends MQTTTransfer {
// —— inicialización del transportador vimpmqtt ———————————— comenzar
	constructor(settings) {
		super(Object.assign({
            serializer: msgPackSerialize,
            deserializer: msgPackDeserialize,
        }, settings));
	}
// —— inicialización del transportador vimpmqtt ———————————— fin
}

module.exports = VIMPMQTTTransfer;
