/*

	Clase base de transporte MQTT

*/

//Clase base del transmisor
const UDPTransfer = require('./udp');

const { msgStructBufferSerialize } = require('../protocols/serializers');
const { msgStructBufferDeserialize } = require('../protocols/vimp/serializers');

class VIMPUDPTransfer extends UDPTransfer {
// —— inicialización del transporte vimpudp ———————————— comenzar
	constructor(settings) {
		super(Object.assign({
            serializer: msgStructBufferSerialize,
            deserializer: msgStructBufferDeserialize,
        }, settings));
	}
// —— inicialización del transporte vimpudp ———————————— fin
}

module.exports = VIMPUDPTransfer;
